import React from 'react';
import './App.css'; // Assuming you have a separate CSS file for styling
import Card from './Components/Card'; // Import the Card component
import BarChart from './Components/BarChart'; // Import the chart components
import LineChart from './Components/LineChart';
import PieChart from './Components/PieChart';
import { UserData } from './Data';

function App() {
  const [userData, setUserData] = React.useState({
    labels: UserData.map((data) => data.year),
    datasets: [
      {
        label: 'Users Gained',
        data: UserData.map((data) => data.userGain),
        backgroundColor: [
          'rgba(75,192,192,1)',
          '#ecf0f1',
          '#50AF95',
          '#f3ba2f',
          '#2a71d0',
        ],
        borderColor: 'black',
        borderWidth: 2,
      },
    ],
  });

  return (
    <div className="App">
      <h1>Blood Donation Statistics</h1>
      <div className='card'>
        <Card title="Bar Chart" content="This is a bar chart">
          <BarChart chartData={userData} />
        </Card>
      </div>
      <div className='card'>
        <Card title="Line Chart" content="This is a line chart">
          <LineChart chartData={userData} />
        </Card>
      </div>
      <div className='card'>
        <Card title="Pie Chart" content="This is a pie chart">
          <PieChart chartData={userData} />
        </Card>
      </div>
    </div>
  );
}

export default App;
